(40, 'wcpay-promo-2021-6-incentive-1', 'marketing', 'en_US', 'Special offer: Save 50% on transaction fees for up to $125,000 in payments', 'Save big when you add <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp222\">WooCommerce Payments</a> to your store today.\n                Get a discounted rate of 1.5% + $0.15 on all transactions – that’s 50% off the standard fee on up to $125,000 in processed payments (or six months, whichever comes first). Act now – this offer is available for a limited time only.\n                <br /><br />By clicking \"Get WooCommerce Payments,\" you agree to the promotional <a href=\"https://woocommerce.com/terms-conditions/woocommerce-payments-half-off-six-promotion/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp222\">Terms of Service</a>.', '{}', 'pending', 'woocommerce.com', '2021-06-08 21:50:09', NULL, 0, 'plain', '', 0, 'info'),
(41, 'wcpay-promo-2021-6-incentive-2', 'marketing', 'en_US', 'Special offer: No transaction fees* for up to three months', 'Save big when you add <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp233\">WooCommerce Payments</a> to your store today. Pay zero transaction fees* on up to $25,000 in processed payments (or three months, whichever comes first). Act now – this offer is available for a limited time only. *Currency conversion fees excluded.\n                <br /><br />By clicking \"Get WooCommerce Payments,\" you agree to the promotional <a href=\"https://woocommerce.com/terms-conditions/woocommerce-payments-no-transaction-fees-for-three-promotion/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp233\">Terms of Service</a>.', '{}', 'pending', 'woocommerce.com', '2021-06-08 21:50:09', NULL, 0, 'plain', '', 0, 'info'),
(42, 'ppxo-pps-upgrade-paypal-payments-1', 'info', 'en_US', 'Get the latest PayPal extension for WooCommerce', 'Heads up! There\'s a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.', '{}', 'pending', 'woocommerce.com', '2021-06-20 12:21:23', NULL, 0, 'plain', '', 0, 'info'),
(43, 'ppxo-pps-upgrade-paypal-payments-2', 'info', 'en_US', 'Upgrade your PayPal experience!', 'We\'ve developed a whole new <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension for WooCommerce</a> that combines the best features of our many PayPal extensions into just one extension.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, offer subscription and recurring payments, and the new PayPal pay later options.<br /><br />Start using our latest PayPal today to continue to receive support and updates.', '{}', 'pending', 'woocommerce.com', '2021-06-20 12:21:23', NULL, 0, 'plain', '', 0, 'info'),
(44, 'eu_vat_changes_2021', 'marketing', 'en_US', 'Get your business ready for the new EU tax regulations', 'On July 1, 2021, new taxation rules will come into play when the <a href=\"https://ec.europa.eu/taxation_customs/business/vat/modernising-vat-cross-border-ecommerce_en\">European Union (EU) Value-Added Tax (VAT) eCommerce package</a> takes effect.<br /><br />The new regulations will impact virtually every B2C business involved in cross-border eCommerce trade with the EU.<br /><br />We therefore recommend <a href=\"https://woocommerce.com/posts/new-eu-vat-regulations\">familiarizing yourself with the new updates</a>, and consult with a tax professional to ensure your business is following regulations and best practice.', '{}', 'unactioned', 'woocommerce.com', '2021-06-21 17:32:38', NULL, 0, 'plain', '', 0, 'info'),
(45, 'wc-admin-orders-milestone', 'info', 'en_US', 'Glückwunsch zur Bearbeitung von 10 Bestellungen!', 'Du hast den Meilenstein von 10 Bestellungen erreicht! Kompliment! Lasse dich von WooCommerce-Erfolgsgeschichten inspirieren.', '{}', 'unactioned', 'woocommerce-admin', '2021-06-25 17:55:07', NULL, 0, 'plain', '', 0, 'info'),
(46, 'wc-gzd-admin-encryption-notice', 'update', 'en_US', 'Schlüssel zur Datenverschlüsselung fehlt', '<p>Achtung! Die <em>WC_GZD_ENCRYPTION_KEY</em> (Allgemein, z.B. von API-Schlüsseln oder Passwörtern in den Einstellungen) Konstante fehlt. Germanized nutzt einen abgeleiteten Schlüssel basierend auf der <em>LOGGED_IN_KEY</em> Konstante stattdessen. Unter bestimmten Umständen kann sich diese Konstante verändern. Um zu verhindern, dass verschlüsselte Daten nicht mehr entschlüsselt werden können, füge bitte folgendes Snippet in deine <a href=\"https://wordpress.org/support/article/editing-wp-config-php/\" target=\"_blank\">wp-config.php</a> Datei ein:</p><p>define( \'WC_GZD_ENCRYPTION_KEY\', \'552af67259f619472a3420c7acc1320843d82761ecae39ae4e826fbfa3a3bfe5\' );</p>', '{}', 'unactioned', 'woocommerce-germanized', '2021-06-24 12:05:29', NULL, 0, 'plain', '', 0, 'info');
